import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";


function Ecomdata() {

    const [data, setData] = useState([])
   

    useEffect(() => {

        fetch("http://localhost:3005/E-commerce")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                console.log(resp)

                setData(resp)
            })
    }, [])

    // search 

    const [value,setValue]=useState("")
    const change=(e)=>{
        setValue(e.target.value)
    }

    // async-await 

const search = async (event) => {
    event.preventDefault()
    return await axios.get(`http://localhost:3005/E-commerce?q=${value}`)

        .then((res) => {
            setData(res.data)
            setValue("")
        })
        .catch(() => {
            alert("error")
        })
}


    return (

        <div className="container-fluid">
            <div className="card1">

            <div className="nav">

                <div>
                    <form onSubmit={search}>

                        <div className="search">
                            <input value={value} type="text"  className="form-control" onChange={change} aria-describedby="emailHelp" />
                        </div>

                    </form>
                </div>
                <div id="logout">

                    <Link to='/Signout' id="link"><i class="fa-regular fa-circle-user"></i> Logout</Link>

                </div>
            </div>

            <div className="ecomdata">

                {data.map((item) => (
                    <div id="data">
                        <p><img src={item.image} style={{ width: "200px", height: "180px" }} /> </p>
                        <h4>{item.brand}</h4>
                        <p>{item.name}</p>
                        <h6><i class="fa-solid fa-indian-rupee-sign"></i> {item.price}</h6>
                        <button id="addcart">Add-cart</button>
                    </div>

                ))}

            </div>

        </div>
        </div>

    )

}
export default Ecomdata;
