import { useEffect, useState } from "react";
// import { Link } from "react-router-dom";
import axios from "axios";


function Search() {

    const [data, setData] = useState([])
   

    useEffect(() => {

        fetch("http://localhost:3005/E-commerce")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                console.log(resp)

                // setData(resp)
            })
    }, [])

    // search 

    const [value,setValue]=useState("")
    const change=(e)=>{
        setValue(e.target.value)
    }

    // async-await 

const search = async (event) => {
    event.preventDefault()
    return await axios.get(`http://localhost:3005/E-commerce?q=${value}`)

        .then((res) => {
            // setData(res.data)
            setValue("")
    
        })
        .catch(() => {
            alert("error")
        })
}


    return (

        <div className="container-fluid">

                <div >
                    <form onSubmit={search}>

                        <div className="search">
                            <input value={value} type="text"  placeholder="Search for Products,Brand and more" className="form-control" onChange={change} aria-describedby="emailHelp" />
                        </div>

                    </form>
                </div>

               
        

            <div className="ecomdata">

                {data.map((item) => (
                    <div id="data">
                        <p><img src={item.image} style={{ width: "180px", height: "150px" }} /> </p>
                        <h4>{item.brand}</h4>
                        <p>{item.name}</p>
                        <h6><i class="fa-solid fa-indian-rupee-sign"></i> {item.price}</h6>
                        <button id="addcart">Add-cart</button>
                    </div>

                ))}

            </div>

        </div>
    

    )

}
export default Search;
