import { useEffect, useState } from "react";



function Addcart() {

    const [data, setData] = useState([])
    useEffect(() => {
        fetch("http://localhost:3005/Add-cart")

            .then((res) => {
                return res.json()
            })

            .then((resp) => {
                setData(resp)
                // console.log(resp)
            })

    }, [])

    // remove 

    const [count, setCount] = useState(0)

    const deleted = (id) => {

        fetch("http://localhost:3005/Add-cart/" + id, {
            method: "DELETE"
        })
            .then(() => {
                window.location.reload()
                setCount(count - 1)
            })
            .catch(() => {
                alert("error")
            })
    }





    return (
        <div className="container-fluid">
            <div className="adddata">
                {data.map((item) => (
                    <div id="cartdata">
                        <div className="datas">
                        <div id="cartimage">
                        <p><img src={item.image} style={{ width: "160px", height: "100px" }} /> </p>
                       </div>
                        <h4>{item.brand}</h4>
                        <p>{item.name}</p>
                        <h6><i class="fa-solid fa-indian-rupee-sign"></i> {item.price}</h6>
                        </div>
                        <div className="cartbtn">
                            <button onClick={() => { deleted(item.id) }} id="remove">Remove</button>
                            <button id="buybtn">Buy now</button>
                        </div>
                    </div>
                    

                ))}
            </div>
        </div>

    )
}
export default Addcart;
