import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";


function Ecomdata() {

    const [data, setData] = useState([])
    const navigate=useNavigate()


    useEffect(() => {

        fetch("http://localhost:3005/E-commerce")
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                console.log(resp)

                setData(resp)
            })
    }, [])

    //     // search 

    //     const [value,setValue]=useState("")
    //     const change=(e)=>{
    //         setValue(e.target.value)
    //     }

    //     // async-await 

    // const search = async (event) => {
    //     event.preventDefault()
    //     return await axios.get(`http://localhost:3005/E-commerce?q=${value}`)

    //         .then((res) => {
    //             setData(res.data)
    //             setValue("")
    //         })
    //         .catch(() => {
    //             alert("error")
    //         })
    // }


    return (

        <div className="container-fluid">
            <div className="card1">

                <div className="nav">
          <div className="homesearch">
          <Link to='/Ecomsearch' id="searchicon">
    <i class="fa-solid fa-magnifying-glass"></i>
    </Link>
            </div>                     
                   
                    <div id="logout">

                        <Link to='/Signout' id="link"><i class="fa-regular fa-circle-user"></i> Logout</Link>

                    </div>
                </div>

                <div className="ecomdata">

                    {data.map((item) => (
                        <div id="data">
                            <p><img src={item.image} style={{ width: "180px", height: "150px" }} /> </p>
                            <h4>{item.brand}</h4>
                            <p>{item.name}</p>
                            <h6><i class="fa-solid fa-indian-rupee-sign"></i> {item.price}</h6>
                            <button id="addcart">Add-cart</button>
                        </div>

                    ))}

                </div>

            </div>
        </div>

    )

}
export default Ecomdata;
