import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";



function EmpEdit() {
    const [data,setData]=useState([])
const navigate=useNavigate()

    useEffect(()=>{
        fetch("http://localhost:3005/empData")
        .then((res)=>{
            return res.json()
        })
        .then((resp)=>{
            // console.log(resp)
        
            setData(resp)
        })
    },[])

const edit=(a)=>{
    navigate("/empedit/"+id)
}
const deleted=(a)=>{
    fetch("http://localhost:3005/empData/"+id),{
        method:"DELETE"
    }
}

    return (
        <div className="container mt-3">
            <div className="card">
                <div className="card-title text-center pt-2">
                    <h3>Employe Management System</h3>
                </div>
                <div className="card-body">
                    
                    <table class="table table-bordred">
                        <thead>
                            <tr>
                                <th scope="col">ID</th>
                                <th scope="col">Name</th>
                                <th scope="col">Email</th>
                                <th scope="col">City</th>
                                <th scope="col">Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {data.map((item)=>(
                                <tr>
                                    <td>{item.id}</td>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.city}</td>
                                    <td>
                                        <a className="btn btn-primary" onClick={()=>{edit(item.id)}}>  edit</a> 
                                        <a className="btn btn-danger" style={{marginLeft:"10px"}} onClick={deleted}>delete</a>
                                        <a className="btn btn-success" style={{marginLeft:"10px"}} >Add</a>
                                    </td>
                                </tr>

                             ))}
                      
                        </tbody>
                      
                    </table>
                    <Link to="/empform" className="btn btn-success ">
                        Add New(+)
                        </Link>
                </div>
            </div>

        </div>
    )
}

export default EmpEdit;
