import { useEffect, useState } from "react";
import { Link } from "react-router-dom";



function Addcart() {

    const [data, setData] = useState([])

    useEffect(() => {
        fetch("http://localhost:3005/Add-cart")

            .then((res) => {
                return res.json()
            })

            .then((resp) => {
                setData(resp)
                // console.log(resp)
            })

    }, [])

    // remove 


    const deleted = (id) => {

        fetch("http://localhost:3005/Add-cart/" + id, {
            method: "DELETE"
        })
            .then(() => {
                window.location.reload()
            })
            .catch(() => {
                alert("error")
            })
    }

    // total price 

    useEffect(()=>{

    })


    const [price, setPrice] = useState([])
    const [sum, setSum] = useState(0)
    

    useEffect(() => {
    
        fetch("http://localhost:3005/Add-cart")

            .then((res) => {
                return res.json()
            })

            .then((resp) => {
                setPrice(resp)
               setSum=sum+resp.price
            })

    }, [])




    return (
        <div className="container-fluid">

            <div className="head">

                <Link to='/Ecomdata' >
                    <i class="fa-solid fa-arrow-left-long"></i>

                </Link>
                <h6 style={{ marginLeft: "100px" }}>Add-Cart</h6>

                <Link style={{ marginLeft: "5px" }} id="link">
                    <i class="fa-solid fa-cart-shopping" ></i> <span >{data.length}</span>
                </Link>

            </div>



            <div className="addcartdata">
                {data.map((item) => (
                    <div id="cartdata">
                        <div className="datas">

                            <div id="cartimage">
                                <p><img src={item.image} style={{ width: "160px", height: "100px" }} /> </p>
                                <p style={{ color: "darkgreen" }}>FREE Delivery</p>
                            </div>

                            <div className="left">
                                <h4>{item.brand}</h4>
                                <p>{item.name}</p>
                                <h6><i class="fa-solid fa-indian-rupee-sign"></i> {item.price}</h6>
                                <p>Delivery in 2 Days</p>

                            </div>
                        </div>
                        <hr />
                        <div className="cartbtn">
                            <button onClick={() => { deleted(item.id) }} id="remove">Remove</button>
                            <button id="buybtn">Buy now</button>
                        </div>
                    </div>
                ))}
     <div>
        
     <p>{sum}</p>
        
        </div>       </div>

            {price.map((data) => (
                <div id="price">
                    <p>{data.price}</p>
                    
                </div>
            ))}
        </div>

    )
}
export default Addcart;
