import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";


function Detail() {
const {id}=useParams()
    const [data, setData] = useState()

    useEffect(() => {
        fetch("http://localhost:3005/E-commerce/" + id)
            .then((res) => {
                return res.json()
            })

            .then((resp) => {
                setData(resp)
                console.log(resp)
            })
    }, [])

    return (

    
             <div className="ecomdata">

{data &&
    <div >

        <p><img src={data.image} id="img" /> </p>
        <h4>{data.brand}</h4>
        <p>{data.name}</p>
        <h6><i class="fa-solid fa-indian-rupee-sign"></i> {data.price}</h6>
        
       
    </div>

}

</div>


    )
}
export default Detail;
