import { useEffect, useState } from "react";



function Addcart() {

    const [data, setData] = useState([])
    useEffect(() => {
        fetch("http://localhost:3005/Add-cart")

            .then((res) => {
                return res.json()
            })

            .then((resp) => {
                setData(resp)
                // console.log(resp)
            })

    })

    // remove 

    const [count, setCount] = useState(0)
    const deleted = (id) => {
        fetch("http://localhost:3005/Add-cart/" + id, {
            method: "DELETE"
        })
            .then(() => {
                // window.location.reload()
                setCount(count - 1)
            })
            .catch(() => {
                alert("error")
            })
    }





    return (
        <div>

            {data.map((item) => (
                <div id="data">
                    <p>{item.id}</p>
                    <p><img src={item.image} style={{ width: "160px", height: "100px" }} /> </p>
                    <h4>{item.brand}</h4>
                    <p>{item.name}</p>
                    <h6><i class="fa-solid fa-indian-rupee-sign"></i> {item.price}</h6>
                    <button onClick={() => { deleted(item.id) }} id="remove">Remove</button>

                </div>

            ))}


        </div>
    )
}
export default Addcart;
