import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

function AddCart() {

    const [data, setData] = useState([])
    const { cart } = useParams()

    useEffect(() => {

        fetch("http://localhost:3005/empData/" + cart)
            .then((res) => {
                return res.json()
            })
            .then((resp) => {
                setData(resp)
                console.log(resp)
            })
    }, [])


   
     

    return (
        <div className="container mt-3">

{data.map((item) => (
                            <tr>
                                <td>{item.id}</td>
                                <td>{item.name}</td>
                                <td>{item.email}</td>
                                <td>{item.city}</td>
                                <td>
                                    <a className="btn btn-primary" onClick={() => { edit(item.id) }}>  edit</a>
                                    <a className="btn btn-danger" style={{ marginLeft: "10px" }} onClick={() => { deleted(item.id) }}>delete</a>
                                    <a className="btn btn-success" style={{ marginLeft: "10px" }} onClick={() => { addcart(item.id) }}>Add</a>
                                </td>
                            </tr>

                        ))}


            {/* <Link to="/" className="btn btn-success ">
                Back
                </Link> */}
        </div>


    )
}
export default AddCart;
