import { useState } from "react";

function App1({red ,green ,onn,off}){

const [color,setColor]=useState(red)
const [data,setData]=useState(onn)

const update=()=>{
    let x=color==red ? green : red
    setColor(x)
}
const change=()=>{
    let y=data==onn ? off : onn
    setData(y)
}


    return(
        <div>
            <h2 style={{
                backgroundColor:color,
                width:"200px", height:"200px",
                borderRadius:"50%",
                // marginLeft:"300px",marginTop:"100px"
            }}></h2>
<button onClick={update}>update</button>
<button onClick={change}>{data}</button>
        </div>
    )
}
export default App1;
