import React from "react"

class App extends React.Component{
    render(){

    let name="hari"
    let age="25"
    
    return(
        <div>
            <h2>app parent</h2>
            { <h2>name:{name} and age:{age}</h2> }
            <Main a={name} b={age}/>
        
        </div>
    )
}
}
function Main(props){
    const {a ,b}= this.props
 
    return(
        <div>
            <h2>main parent </h2>
            { <h2>name:{a} and age:{b}</h2> }
            <Tech c={a} d={b}/>
    
        </div>
    )
}
