import { useState } from "react";
import { initializeApp } from "firebase/app";
import { signInWithEmailAndPassword, getAuth } from "firebase/auth";
import { Link, useNavigate } from "react-router-dom";


function Login() {

    const [email, setEmail] = useState("")
    const [pswd, setPswd] = useState("")
    const navigate=useNavigate()


    const firebaseConfig = {
        apiKey: "AIzaSyDAm1l6txCkIKB3EQsvt-L-6qgluOJpfhI",
        authDomain: "auth-cc868.firebaseapp.com",
        projectId: "auth-cc868",
        storageBucket: "auth-cc868.appspot.com",
        messagingSenderId: "431111410332",
        appId: "1:431111410332:web:38ab3e03f6c3777f631359",
        measurementId: "G-GV6YBQQM6B"
    };

    const app = initializeApp(firebaseConfig);
    const auth = getAuth()


    const submit=(event) => {
        event.preventDefault()

        let obj = {
            email: email,
            password: pswd
        }
        signInWithEmailAndPassword(auth, obj.email, obj.password)

            .then(() => {
                alert("successfully Login...")
                navigate("/logout")
            })
            .catch(() => {
                alert("error...")
            })
    }


    return (
        <div className="container my-5" style={{ width: "400px" }}>
            <div className="card p-4">
                <div className="card-title">
                    <h3 className="text-center">Signup</h3>
                </div>

                <div card-body>
                    <form onSubmit={submit}>  
 
                        <div class="mb-3">
                            <label class="form-label">Email </label>
                            <input value={email} type="email" onChange={e => setEmail(e.target.value)} required class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                        </div>
                        <div class="mb-3">
                            <label class="form-label">Password</label>
                            <input value={pswd} type="password" onChange={e => setPswd(e.target.value)} required class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                        </div>

                        <button type="submit" class="btn btn-primary">Login</button>

                    </form>
                    {/* <div>
                        <p>if you domn't have a account
                            <Link to='/'>Register</Link>
                        </p>
                    </div> */}


                </div>


            </div>





        </div>
    )
}
export default Login;
